define(["jquery","core/modal","core/notification"],function($,Modal,Notification){var frontpage={add_block:function(lang){$("#frontpage_add_block").click(function(){Modal.create({title:$("#frontpage_add_block_modal").attr("data-title"),body:'<div id="list-models" class="d-flex flex-wrap row"></div>',large:true,show:true,removeOnClose:true}).then(function(modal){if(!modal.root){modal.root=modal._root}modal.modal.addClass("modal-dialog-centered");frontpage.add_block_modal_init(modal)}).catch(Notification.exception)})},add_block_modal_init:function(modal){async function loadFiles(){const response=await fetch(`${M.cfg.wwwroot}/theme/boost_training/_editor/model/?lang=${lang}`);if(!response.ok){throw new Error("Error loading files: "+response.status)}const $list=$("#list-models");$list.empty();const data=await response.json();const groups={};data.forEach(function(item){const type=item.id.includes("-")?item.id.split("-")[0]:item.id;if(!groups[type])groups[type]=[];groups[type].push(item)});Object.values(groups).forEach(function(grupo){let width="";if(grupo.length===4||grupo.length===8){width="col-md-3"}else if(grupo.length===3||grupo.length===6){width="col-md-4"}else if(grupo.length===2){width="col-md-6"}else if(grupo.length===1){width="col-md-12"}grupo.forEach(function(item){const block=$(`
                            <div class="item-model ${width} text-center" role="button">
                                <div class="item-model-border">
                                    <img src="${item.image}" 
                                         alt="${item.title}" 
                                         class="img-fluid mb-2" style="width:100%;border:1px solid #ccc;border-radius:8px;">
                                    <h5 style="font-size: 1rem;">${item.title}</h5>
                                    <a href="${item.preview}" 
                                       target="_blank">${M.util.get_string("preview","theme_boost_training")}</a>
                                </div>
                            </div>`);$list.append(block);block.find("a").click(function(){event.stopImmediatePropagation()});block.click(function(){location.href=`${M.cfg.wwwroot}/theme/boost_training/_editor/editor.php?lang=${lang}&local=home&dataid=create&template=${item.id}`})})})}loadFiles()}};return frontpage});